<script>
import {reactive} from 'vue'
import user_api from '@/api/user'

export default {
  name: 'LoginView',
  setup() {
    const loginForm = reactive({
      username: '',
      password: ''
    })

    const loginRules = {
      username: [{required: true, message: '请输入账号', trigger: 'blur'}],
      password: [{required: true, message: '请输入密码', trigger: 'blur'}]
    }

    const loading = reactive(false)

    const login = () => {
      loading.value = true
      // TODO: 发送登录请求
    }

    return {
      loginForm,
      loginRules,
      loading,
      login
    }
  },
  mounted() {
    this.get_wx_qrcode();
  },
  methods: {
    get_wx_qrcode() {
      new WxLogin({
        id: "wx_login_container",
        appid: "wx53cf447461989356",
        scope: "snsapi_login",
        redirect_uri: encodeURIComponent("https://www.template.matrix-studio.top/login"),
        style: "black",
        href: "",
        self_redirect: false,
      });
      this.check_wx_qrcode();
    },
    check_wx_qrcode() {
      this.checkQRCodeInterval = setInterval(() => {
        if (this.$route.query.code) {
          clearInterval(this.checkQRCodeInterval);
          user_api.wx_login(this.$route.query.code).then(res => {
            console.log(res);
            alert(res);
          }).catch(err => {
            console.log(err);
          })
        }
      }, 1000);
    },
  }
}
</script>

